import React from 'react';
import { useRef } from 'react';
import './scroll.css';
import ScrollToTop from '../scrolltotop';

const Scroll = () => {
    const services = useRef(null);
    const blog = useRef(null);
    const contact= useRef(null);

    const scrollToSection = (elementRef) => {
        window.scrollTo({
            top: elementRef.current.offsetTop,
            behavior: 'smooth',
        })
    }

    return (
        <div className="scroll">
            <ScrollToTop />
            <div className='hero'>
            <ul>
                <li onClick={() => scrollToSection(services)} className='link'>services</li>
                <li onClick={() => scrollToSection(blog)} className='link'>blog</li>
                <li onClick={() => scrollToSection(contact)} className='link'>contact</li>
            </ul>
        </div>
        <div ref={services} className='services'>
        <h3>Services</h3>
        </div>
        <div ref={blog} className='blog'>
        <h3>blog</h3>
        </div>
        <div ref={contact} className='contact'>
        <h3>contact</h3>
        </div>
        </div>
    )
}

export default Scroll;
