import React from 'react';
import { useRef } from 'react';
import './scroll.css';
import logo from '../../asset/realnet.png';
import contactImg from '../../asset/contact.png'
import ScrollToTop from '../scrollToTop/scrolltotop';

const Scroll = () => {
    const Home = useRef(null);
    const blog = useRef(null);
    const contact= useRef(null);

    const scrollToSection = (elementRef) => {
        window.scrollTo({
            top: elementRef.current.offsetTop,
            behavior: 'smooth',
        })
    }

    return (
        <div className="scroll">
            <ScrollToTop />
            <a href='https://example.com' target='blank'>
            <img src={logo} alt='Logo' className='logo'/>
            </a>
            <div className='hero'>
            <ul>
                <li onClick={() => scrollToSection(home)} className='link'>home</li>
                <li onClick={() => scrollToSection(blog)} className='link'>blog</li>
                <li onClick={() => scrollToSection(contact)} className='link'>contact</li>
            </ul>
        </div>
        <div ref={home} className='servic'>
        <h3>Services</h3>
        </div>
        <div ref={blog} className='blog'>
        <h3>blog</h3>
        </div>
        <div ref={contact} className='contact'>
        <h3>contact</h3>
        </div>
        </div>
    )
}

export default Scroll;
