import React from 'react';
import { useRef } from 'react';
import './scroll.css';
import logo from '../../asset/realnet.png';
import contactImg from '../../asset/contact.png'
import ScrollToTop from '../scrollToTop/scrolltotop';

const Scroll = () => {
    const Home = useRef(null);
    const About = useRef(null);
    const Portfolio= useRef(null);
    const Contact= useRef(null);

    const scrollToSection = (elementRef) => {
        window.scrollTo({
            top: elementRef.current.offsetTop,
            behavior: 'smooth',
        })
    }

    return (
        <div className="scroll">
            <ScrollToTop />
            <a href='https://example.com' target='blank'>
            <img src={logo} alt='Logo' className='logo'/>
            </a>
            <div className='hero'>
            <ul>
                <li onClick={() => scrollToSection(Home)} className='link'>Home</li>
                <li onClick={() => scrollToSection(About)} className='link'>About</li>
                <li onClick={() => scrollToSection(Portfolio)} className='link'>Portfolio</li>
                <li onClick={() => scrollToSection(Portfolio)} className='link'>Portfolio</li>
            </ul>
        </div>
        <div ref={Home} className='Home'>
        <h3>Home</h3>
        </div>
        <div ref={About} className='About'>
        <h3>About</h3>
        </div>
        <div ref={Portfolio} className='Portfolio'>
        <h3>Portfolio</h3>
        </div>
        </div>
    )
}

export default Scroll;
